class postComments{constructor(e){this.postId=e,this.postContainer=$(`#each-post-${e}`),this.commentForm=$(`#post-${e}-comment-form`),this.createComment(e);let t=this;$(" #delete-comment",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.commentForm.submit(function(n){n.preventDefault();$.ajax({type:"POST",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" #delete-comment",o)),new ToggleLike($(" .toggle-like-btn",o)),notyNotification("Comment added","success")},error:function(e){notyNotification(e,"error")}})})}newCommentDom(e){return $(`<div id="each-comment-${e._id}" class="each-comment">\n                    <div id="info">\n                        <i class="fas fa-user"></i>\n                \n                        <div id="comment-info">\n                            <span id="commented-by">${e.user.name}</span>\n                            <span id="commented-at">On ${e.createdAt.toString().substring(0,15)}</span>\n                        </div>\n                \n                        <a href="/comments/delete/${e._id}" id="delete-comment">delete</a>\n                \n                    </div>\n                    <div id="content">\n                        ${e.content}\n                    </div>\n\n                    <div id="likes">\n                        <a class="toggle-like-btn" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                            <i class="fas fa-thumbs-up"></i>\n                            <span>0 Likes</span>\n                        </a>\n                        \n                    </div>\n\n                </div>`)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"GET",url:$(e).prop("href"),success:function(e){$(`#each-comment-${e.data.comment_id}`).remove(),notyNotification("Comment deleted","success")},error:function(e){notyNotification(e,"error")}})})}}let notyNotification=function(e,t){new Noty({theme:"relax",text:e,type:t,layout:"topRight",timeout:1500}).show()};