class ToggleFriendship{constructor(e){this.toggler=e,this.toggleFriendship()}toggleFriendship(){let e=this;$(this.toggler).click(function(t){t.preventDefault();let o=this;$.ajax({type:"GET",url:$(o).attr("href")}).done(function(t){0==t.data.removed?($(o).html("Remove Friend"),e.notyNotification("Added to Your Friends","success")):($(o).html("Add Friend"),e.notyNotification("Removed from Your Friends","success"))}).fail(function(e){console.log("Error in completing the request")})})}notyNotification(e,t){new Noty({theme:"relax",text:e,type:t,layout:"topRight",timeout:1500}).show()}}class Friendship{constructor(e){this.element=e,this.removeElement()}removeElement(){$(this.element).click(function(e){e.preventDefault();$.ajax({type:"GET",url:$(this).attr("href")}).done(function(e){$(`#each-user-${e.data.user_id}`).remove(),notyNotification("Removed from Your Friends","success")}).fail(function(e){console.log("Error in completeing the request")})})}}